import React, { Component } from "react";
import { Grid, Row, Col, Table } from "react-bootstrap";
import axios from 'axios'

import Card from "components/Card/Card.jsx";
// import { thArray, tdArray } from "variables/Variables.jsx";


const thArray = ["Ticker", "Open", "Close", "Bull"];
class CurrentPrices extends Component {

  constructor(props){
    super(props);

    this.state = {
      data: [["", "", "", "", ""]],
    }
  }

  componentDidMount(){
    const axiosSp = axios.create({
      baseURL: 'https://seniorprojectu.herokuapp.com/graphql',
    });

    const getTrades = 
    `
    query{pricesUniverse { ticker openPrice closePrice bull}}
    `

    const tweetData = [];

    // loop through all the companies
    // this.state.companyList.map(company => {
    //     const companyTweets = 
    //     `
    //     query{
    //       recentTweets(ticker: "${company}"){
