    axiosSp
    .post('', {query: getTrades})
    .then(result  => {
        console.log(result);
      // this.setState({data: result})
      const tweets = result['data']['data']['pricesUniverse'];
      console.log(tweets)
      const a = tweets.map(a => {
        let b = []
        b.push(a.ticker)
        b.push(a.openPrice)
        b.push(a.closePrice)
        a.bull ? b.push("True") : b.push("False");
        // b.push(a.bull)

        tweetData.push(b)
      });
      console.log(tweetData);
      this.setState({data: tweetData})
    }
    );
  }

  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col md={12}>
              <Card
                title="Current Price Data"
                category="Most Recent Stock Prices"
                ctTableFullWidth
                ctTableResponsive
                content={
                  <Table striped hover>
                    <thead>
                      <tr>
                        {thArray.map((prop, key) => {
                          return <th key={key}>{prop}</th>;
                        })}
                      </tr>
                    </thead>
                    <tbody>
                      {this.state.data.map((prop, key) => {
                        return (
                          <tr key={key}>
                            {prop.map((prop, key) => {
                              return <td key={key}>{prop}</td>;
                            })}
                          </tr>
                        );
