import React, { Component } from "react";
import ChartistGraph from "react-chartist";
import { Grid, Row, Col } from "react-bootstrap";
import ApolloClient from 'apollo-boost';
import { gql } from 'apollo-boost';
import axios from 'axios'
import { Card } from "components/Card/Card.jsx";
import { StatsCard } from "components/StatsCard/StatsCard.jsx";
// import { Tasks } from "components/Tasks/Tasks.jsx";
import {
  dataPie,
  legendPie,
  dataSales,
  optionsSales,
  responsiveSales,
  legendSales,
  dataBar,
  optionsBar,
  responsiveBar,
  legendBar
} from "variables/Variables.jsx";
import { get } from "https";
import { requiredSubselectionMessage } from "graphql/validation/rules/ScalarLeafs";
import { number } from "prop-types";

class Dashboard extends Component {

  constructor(props){

    super(props);

    this.state = {
      tweetCount: 0
    }

  }

  setNumberOfTweets(count){
    this.setState({
      tweetCount: count,
    })
  };

  componentDidMount(){

    const axiosSp = axios.create({
      baseURL: 'https://seniorprojectu.herokuapp.com/graphql',
    });

    const getData = `
    query{pricesUniverse { ticker openPrice closePrice bull}}
    `;

    const getTweets = 
    `
    query{
      recentTweets(ticker:"Amazon"){
              tweet
            rating
          }
      }
    `


    // for weights chart of single graph
    const weights = `
    query{
      RecentWeights(ticker: "TSLA", count: 15) {
          ticker
          fourWeight
          profitWeight
          twitterWeight
          movingWeight
          companyWeight
          date
      }
  }
`;

    const positions = `
      query{
        GetPositions {
          ticker
        }
      }
    `;

    const tweetCount = `
      query{
        TweetCount
      }
    `;

    const pricePagination = `
      query{
        pricePagination(page: 1, perPage: 20, sort: DATE_ASC) {
          count
          items {
            ticker
          }
        }
      }
    `;

    const getPositions = `
    query{
      getPositions
    } 
    `


    const getWeight = 
    `query{
      MostRecentWeight(ticker:"TSLA"){
        ticker
        twitterWeight
        fourWeight
        profitWeight
        movingWeight
        companyWeight
      }
    }
    `

    const tweetMany = 
    `query{
      tweetMany(filter:{company:"GOOGL"}){
      company
      tweet
      rating
    }
  }
    `


    let numberOfTweets = 0;
    axiosSp
    .post('', {query: tweetCount})
    .then(result => numberOfTweets = result["data"]["data"]["TweetCount"]);

    this.setNumberOfTweets(numberOfTweets);
    // this.setState({tweetCount: numberOfTweets});

    axiosSp
    .post('', {query: pricePagination})
    .then(result => console.log(result));


    // const client = new ApolloClient({
    //   uri: 'https://seniorprojectu.herokuapp.com/graphql',
    //   fetchOptions: {
    //     mode: "no-cors",
    //     headers: { 'Content-Type': 'application/json' },
		// 	},
    // })

    // client
    // .query({
    //   query: gql`
    //     query{pricesUniverse {ticker openPrice closePrice}}
    //   `
    // })
    // .then(result => console.log(result));
  }

  createLegend(json) {
    var legend = [];
    for (var i = 0; i < json["names"].length; i++) {
      var type = "fa fa-circle text-" + json["types"][i];
      legend.push(<i className={type} key={i} />);
      legend.push(" ");
      legend.push(json["names"][i]);
    }
    return legend;
  }
  render() {
    return (
      <div className="content">
        <Grid fluid>
          <Row>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-wallet text-success" />}
                statsText="Portfolio Value"
                statsValue="$102,013"
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="fa fa-dollar text-success" />}
                statsText="Profit"
                statsValue="$1,345"
                statsIcon={<i className="fa fa-calendar-o" />}
                statsIconText="Last day"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="pe-7s-graph1 text-danger" />}
                statsText="Trades"
                statsValue="23"
                statsIcon={<i className="fa fa-clock-o" />}
                statsIconText="In the last hour"
              />
            </Col>
            <Col lg={3} sm={6}>
              <StatsCard
                bigIcon={<i className="fa fa-twitter text-info" />}
                statsText="Tweets Analyzed"
                statsValue="+45"
                statsIcon={<i className="fa fa-refresh" />}
                statsIconText="Updated now"
              />
            </Col>
          </Row>
          <Row>
            <Col md={8}>
              <Card
                statsIcon="fa fa-history"
                id="chartHours"
                title="Portfolio Value"
                category="All time performance"
                stats="Updated 3 minutes ago"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={dataSales}
                      type="Line"
                      options={optionsSales}
                      responsiveOptions={responsiveSales}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{this.createLegend(legendSales)}</div>
                }
              />
            </Col>
            <Col md={4}>
              <Card
                statsIcon="fa fa-clock-o"
                title="Machine Learning Statistics"
                category="Last Campaign Performance"
                stats="Campaign sent 2 days ago"
                content={
                  <div
                    id="chartPreferences"
                    className="ct-chart ct-perfect-fourth"
                  >
                    <ChartistGraph data={dataPie} type="Pie" />
                  </div>
                }
                legend={
                  <div className="legend">{this.createLegend(legendPie)}</div>
                }
              />
            </Col>
          </Row>

          <Row>
            <Col md={6}>
              <Card
                id="chartActivity"
                title="Profit Loss"
                category="Top 10 stocks"
                stats="Data information certified"
                statsIcon="fa fa-check"
                content={
                  <div className="ct-chart">
                    <ChartistGraph
                      data={dataBar}
                      type="Bar"
                      options={optionsBar}
                      responsiveOptions={responsiveBar}
                    />
                  </div>
                }
                legend={
                  <div className="legend">{this.createLegend(legendBar)}</div>
                }
              />
            </Col>

            {/* <Col md={6}>
              <Card
                title="Tasks"
                category="Backend development"
                stats="Updated 3 minutes ago"
                statsIcon="fa fa-history"
                content={
                  <div className="table-full-width">
                    <table className="table">
                      <Tasks />
                    </table>
                  </div>
                }
              />
            </Col> */}
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Dashboard;
